import React from 'react';
import { render, screen, fireEvent } from '@testing-library/react';
import Moves from '../../../../src/components/moves/Moves';

describe('Moves Component Tests', () => {
  // Mock function for the jumpTo handler
  const mockJumpTo = jest.fn();

  // History prop for the Moves component
  const history = [
    { squares: Array(9).fill(null) },
    { squares: ['X', null, null, null, null, null, null, null, null] },
    { squares: ['X', 'O', null, null, null, null, null, null, null] }
  ];

  beforeEach(() => {
    // Render the Moves component before each test
    render(<Moves history={history} jumpTo={mockJumpTo} />);
  });

  test('renders correct number of move buttons', () => {
    // Test for rendering the correct number of buttons
    const buttons = screen.getAllByRole('button');
    expect(buttons).toHaveLength(history.length);
    expect(screen.getByText('Go to game start')).toBeInTheDocument();
    expect(screen.getByText('Go to move #1')).toBeInTheDocument();
    expect(screen.getByText('Go to move #2')).toBeInTheDocument();
  });

  test('calls jumpTo function with correct move index when button clicked', () => {
    // Test for checking if jumpTo function is called with the correct index
    const button = screen.getByText('Go to move #2');
    fireEvent.click(button);
    expect(mockJumpTo).toHaveBeenCalledWith(2);
  });
});

