import React from 'react';
import { render, fireEvent } from '@testing-library/react';
import '@testing-library/jest-dom/extend-expect';
import Square from '../../../../src/components/square/Square';

describe('Square Component Tests', () => {
  test('renders the button with text from props', () => {
    render(<Square value="X" />);
    const buttonElement = screen.getByRole('button');
    expect(buttonElement).toBeInTheDocument();
    expect(buttonElement).toHaveTextContent('X');
  });

  test('button click should call onClick handler', () => {
    const onClick = jest.fn();
    render(<Square value="X" onClick={onClick} />);
    const buttonElement = screen.getByRole('button');
    fireEvent.click(buttonElement);
    expect(onClick).toHaveBeenCalled();
  });

  test('renders correctly with no initial props', () => {
    render(<Square />);
    const buttonElement = screen.getByRole('button');
    expect(buttonElement).toHaveTextContent('');
  });
});

