import React from 'react';
import { render, screen, fireEvent } from '@testing-library/react';
import Square from '../../../../src/components/square/Square';

describe('Square Component Tests', () => {
  test('renders Square component correctly', () => {
    render(<Square value="X" />);
    expect(screen.getByText('X')).toBeInTheDocument();
  });

  test('calls provided onClick event on click', () => {
    const onClick = jest.fn();
    render(<Square value="O" onClick={onClick} />);
    fireEvent.click(screen.getByText('O'));
    expect(onClick).toHaveBeenCalledTimes(1);
  });

  test('handles onClick being null without crashing', () => {
    const { container } = render(<Square value="X" onClick={null} />);
    fireEvent.click(screen.getByText('X')); // Should not throw error
    expect(screen.getByText('X')).toBeInTheDocument();
  });

  test('renders correctly with undefined value and onClick not throwing errors', () => {
    render(<Square />);
    const button = screen.queryByText(/^\s*$/); // Click on the blank button
    if (button) fireEvent.click(button);
    expect(screen.queryByText(/^\s*$/)).toBeInTheDocument();
  });

  test('renders Square with leading and trailing whitespace correctly', () => {
    render(<Square value=" X " />);
    expect(screen.getByText(' X ')).toBeInTheDocument();
  });
});

